mapper.select:
SELECT * FROM message_wall
WHERE is_delete = 0

    AND scenic_spot_id = #{params.scenicSpotId}


    AND user_id = #{params.userId}


    AND message_type = #{params.messageType}


    AND status = #{params.status}


    AND is_barrage = #{params.isBarrage}


    AND content LIKE CONCAT('%', #{params.keyword}, '%')

ORDER BY create_time DESC

mapper.update:
UPDATE message_wall
SET likes = likes + #{increment}
WHERE id = #{messageId} AND is_delete = 0
